/* eslint-disable no-unused-vars */
import React from "react";
import { useState } from "react";
import { Container } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { TextDecrypt } from "../content/TextDecrypt";

import './Works.css';

// Import ../../assets/recentprojects/
import Portfolio from '../../assets/recentprojects/react-portfolio.png';
import CastMe from '../../assets/recentprojects/castme.png';
import ScanHub from '../../assets/recentprojects/scanhub.png';
import Hebo from '../../assets/recentprojects/hebo.png';
import Crecer from '../../assets/recentprojects/crecer.png';

const useStyles = makeStyles((theme) => ({
  main: {
    maxWidth: '100vw',
    marginTop: '3em',
    marginBottom: "auto",
  },
}));

export const Works = () => {
  const classes = useStyles();
  const [projects, setProjects] = useState([
    { 
      id: 1,
      title: 'Portafolio', 
      description: `Proyecto personal desarrollado usando ReactJS para mostrar mi información laboral.`,
      alter: 'LoFo Project',
      image: `${Portfolio}`,
    },
    { 
      id: 2,
      title: 'AFP CRECER', 
      description: `App para iOS desarrollada para la aseguradora Crecer de El Salvador en donde se pueden consultar y solicitar los diferentes servicios de la empresa.
      App diseñada para el uso de los clientes.`,
      alter: 'LoFo Project',
      image: `${Crecer}`,
    },
    { 
      id: 3,
      title: 'CastMe for IOS and Android', 
      description: `App para IOS y Android desarrollada trabajando para Innovated Software utilizando Xcode con Swift 5 y Java con Android Studio.
      Reproductor en streaming de Podcast/Radio en primer y segundo plano con capacidad de suscribirse a canales, buscar por podcast o artista y adaptable a diferentes pantallas y versiones de los SO.`,
      alter: 'React Portfolio',
      image: `${CastMe}`,
    },
    { 
      id: 4,
      title: 'ScanHub for IOS and Android', 
      description: `App para IOS y Android desarrollada trabajando para Innovated Software utilizando Xcode con Swift 5 y Java con Android Studio. 
      Escaner de documentos para los dispositivos móviles en el que puedes tomar una foto o elegir de la galeria para aplicarle filtros y convertirlo en un documento escaneado.`,
      alter: 'VeriTru Project',
      image: `${ScanHub}`,
    },
    { 
      id: 5,
      title: 'Hebo CLP', 
      description: `Proyecto personal desarrollado para Android utilizando Java y Android Studio IDE.
      App para el restaurante El Mexicano en donde puedes consultar su menú, dirección y puedes ordenar desde tu celular sin la necesidad de un mesero que te tome la orden.`,
      alter: 'LoFo Project',
      image: `${Hebo}`,
    }
  ]);

  return (
    <section id="works">
      <Container component="main" className={classes.main} maxWidth="md">
        {projects.map((project) => (
          <div className="project" key={ project.id }>
            <div className="__img_wrapper">
              <img src={ project.image } alt={ project.alter }/>
            </div>
            <div className="__content_wrapper">
              <h3 className="title">
                <TextDecrypt text={ project.id + '. ' + project.title } />
              </h3>
              <p className="description">
                { project.description }
              </p>
            </div>
          </div>
        ))}
      </Container>
    </section>
  );
};
