import React from 'react';
import { LogoLink } from '../components/logo/LogoLink';
import { Content } from '../components/content/Content';
import { Hidden } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import DisplacementSphere from '../components/background/DisplacementSphere';
import { ThemeToggle } from '../components/theme/ThemeToggle';
import { Resume } from '../components/resume/Resume';
import { SocialIcons } from '../components/content/SocialIcons';
import { SpeedDials } from '../components/speedDial/SpeedDial';
import { SideNavbar } from '../components/nav/SideNavbar';
import { Works } from '../components/works/Works';
import { About } from '../components/about/About';
import { Contact } from '../components/contact/Contact';



const languages = [
  { code: 'en', name: 'English'},
  { code: 'esp', name: 'Español'}
]

const translations = {
  'en': {
    'header': 'Spring',
    'paragraf': 'Hello',
    'lang-choice': 'Choose your language:'
  },
  'esp': {
    'header': 'Verano',
    'paragraf': 'hola',
    'lang-choice': 'Elige el idioma:'
  }
}

const getTranslation = (lang, text) => {
  return translations[lang][text];
}

class LanguageSwitcher extends React.Component {
  state = { 
    lang: 'esp' 
  };

  changeLanguageHandler = (lang) => {
    this.setState({ lang: lang });
  }

  render() {
    return (
      <div className="languageSwitcher">
        <div className="flower"></div>
        <p>
          {getTranslation(this.state.lang, 'paragraf')}
        </p>
        <h2>
          {getTranslation(this.state.lang, 'header')}  
        </h2>
        <p className="small">
          {getTranslation(this.state.lang, 'lang-choice')}
        </p>
        <LanguageSwitcherSelector 
          lang={this.state.lang}
          handleChangeLanguage={this.changeLanguageHandler}
        />
      </div>
    )
  }
}

class LanguageSwitcherSelector extends React.Component {
  onChange = e =>{
    this.props.handleChangeLanguage(e.target.className);
  }

  render() {
    const options = languages.map(language => {
      if(language.code != this.props.lang){
        return <li onClick={this.onChange}><div value={language.code} className={language.code} ></div></li>
      }  
    });

    return (
      <div className="lang"> 
        <div 
          className={this.props.lang}
        >
        </div>
        <ul class="dropdown" >
          {options}
        </ul>
      </div>
    )
  }
}
const useStyles = makeStyles(() => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
  },
}));

export const Home = () => {
  const classes = useStyles();

  return (
    <>
      <div className={classes.root} id="home">
        <DisplacementSphere />
        <LogoLink />
        <Content />
        <ThemeToggle />
        <Hidden smDown>
          <SocialIcons />
        </Hidden>
        <Hidden mdUp>
          <SpeedDials />
        </Hidden>
        <Resume />
      </div>
      <LanguageSwitcher />, 
      <SideNavbar />
      <div className='about-box'>
      <h1 className='titles' id="aboutTitle">Acerca de mi</h1>
      <About />
      </div>
      <div className='works-box'>
      <h1 className='titles' id="worksTitle">Proyectos</h1>
      <Works />
      </div>
      <Contact />
    </>
  );
};
