/* eslint-disable no-unused-vars */
import React from "react";
import { Container, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { TextDecrypt } from "../content/TextDecrypt";
import { FirstName, LastName } from "../../utils/getName";

import './About.css';

import profile from '../../assets/profile.png';

const useStyles = makeStyles((theme) => ({
  main: {
    maxWidth: '100vw',
    marginTop: '3em',
    marginBottom: "auto",
  },
}));

export const About = () => {
  const classes = useStyles();
  const greetings = "Hello there!";
  const aboutme = `Soy ${FirstName} ${LastName}, ingeniero mecatrónico, he trabajado como desarrollador de aplicaciones móviles para IOS y Android.
  Sé sobre desarrollo de software, automatización, programación de PLCs, electrónica, robótica, mecánica, ensamble de PC, desarrollo web y nutrición.

  Actualmente estoy aprendiendo el desarrollo de apps con Reactjs / HTML / CSS / JS, mi interes sería desarrollar FRONTEND de webs o apps.

  Soy una persona autodidacta, por lo que siempre busco y aprendo.

  Mi portafolio incluye proyectos sobre mecatrónica, desarrollo de software así como mis proyectos y logros personales.`;

  return (
    <section id="about">
      <Container component="main" className={classes.main} maxWidth="md">
        <div className="about">
          <div className="_img"
            style={{ 
              background: "url(" + profile + ")",
              backgroundSize: 'contain',
              backgroundPosition: 'center',
              backgroundRepeat: 'no-repeat',
            }}
          >
          </div>
          <div className="_content_wrapper">
            <Typography component='h2' variant="h5">
              <TextDecrypt text={`${greetings}`} />
            </Typography>
            <p className="aboutme">
              {aboutme}
            </p>
            <a href="#contact" className="contact-btn">
              <i className="fas fa-terminal"></i>
              <Typography component='span'> Enviame mensaje.</Typography>
            </a>
          </div>
        </div>
      </Container>
    </section>
  );
};
